import React, { useState, useEffect } from "react";

export default function ChartSection() {
  const [location, setLocation] = useState("California");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchChart = async () => {
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5000/chart?location=${location}`);
      const blob = await response.blob();
      const imageObjectURL = URL.createObjectURL(blob);
      setImageUrl(imageObjectURL);
    } catch (err) {
      console.error("Error fetching chart:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchChart();
  }, [location]);

  return (
    <div className="w-full max-w-4xl mx-auto mt-12 bg-white rounded-xl shadow p-6">
      <h2 className="text-2xl font-bold text-blue-800 mb-4">Dynamic Earthquake Chart</h2>

      <div className="flex items-center gap-4 mb-4">
        <label htmlFor="location" className="font-medium text-gray-700">Select Location:</label>
        <select
          id="location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="p-2 border border-gray-300 rounded-md shadow-sm"
        >
          <option value="California">California</option>
          <option value="Japan">Japan</option>
          <option value="Mexico">Mexico</option>
        </select>
      </div>

      {loading ? (
        <p className="text-gray-500">Generating chart...</p>
      ) : (
        <img
          src={imageUrl}
          alt={`${location} Chart`}
          className="w-full max-w-3xl rounded shadow-md mx-auto"
        />
      )}
    </div>
  );
}
