import React, { useState } from "react";

export default function ChartSection() {
  const [location, setLocation] = useState("California");
  const [inputValue, setInputValue] = useState("California");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchChart = async () => {
    if (!inputValue.trim()) return;
    setLoading(true);
    try {
      const response = await fetch(`http://localhost:5000/chart?location=${encodeURIComponent(inputValue)}`);
      if (!response.ok) throw new Error("Failed to generate chart");
      const blob = await response.blob();
      const imageObjectURL = URL.createObjectURL(blob);
      setImageUrl(imageObjectURL);
      setLocation(inputValue);
    } catch (err) {
      console.error("Error fetching chart:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="w-full max-w-4xl mx-auto mt-12 bg-white rounded-xl shadow p-6">
      <h2 className="text-2xl font-bold text-blue-800 mb-4">Dynamic Earthquake Chart</h2>

      <div className="flex flex-col md:flex-row items-center gap-4 mb-4">
        <label htmlFor="location" className="font-medium text-gray-700">
          Enter Location:
        </label>
        <input
          id="location"
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          placeholder="e.g. California, Japan, Mexico"
          className="p-2 border border-gray-300 rounded-md shadow-sm w-full md:w-1/2"
        />
        <button
          onClick={fetchChart}
          className="px-4 py-2 bg-blue-600 text-white rounded-md font-semibold hover:bg-blue-700 transition"
        >
          Generate
        </button>
      </div>

      {loading ? (
        <p className="text-gray-500">Generating chart for {inputValue}...</p>
      ) : imageUrl ? (
        <img
          src={imageUrl}
          alt={`${location} Chart`}
          className="w-full max-w-3xl rounded shadow-md mx-auto"
        />
      ) : null}
    </div>
  );
}
